var url = 'http://127.0.0.1:8000/orders/getFoodProducts'; // Reemplazar con la URL de tu API de productos

var request = new XMLHttpRequest();
request.open('GET', url, true);
request.onload = function() {
  if (request.status >= 200 && request.status < 400) {
    var response = JSON.parse(request.responseText);
    var food = response; // Supongamos que la respuesta contiene directamente la lista de productos

    // Generar una cadena de HTML con las tarjetas de producto
    var foodHTML = food.map(function(food) {
      return `
          <div class="col-md-4">
              <div class="card h-100 mb-3">
              <img src="${food.productImage}" class="card-img-top imgCategorias" alt="${food.name}">
              <div class="card-body">
                  <h1 class="card-title">${food.name}</h1>
                  <h4 class="card-text">${food.description}</h4>
                  <h5 class="card-text">Precio: $${food.sellPrice}</h5>
              </div>
              </div>
          </div>
      `;
    }).join('');

    // Obtener la referencia al contenedor de productos
    var foodList = document.getElementById('food-list');

    // Insertar las tarjetas de producto en el contenedor
    foodList.innerHTML = foodHTML;
  } else {
    console.error('Error al realizar la solicitud.');
  }
};
request.onerror = function() {
  console.error('Error de conexi√≥n.');
};
request.send();