var url = 'http://127.0.0.1:8000/orders/getcategories'; // Reemplazar con la URL de tu API de productos

var request = new XMLHttpRequest();
request.open('GET', url, true);
request.onload = function() {
  if (request.status >= 200 && request.status < 400) {
    var response = JSON.parse(request.responseText);
    var categorias = response; // Supongamos que la respuesta contiene directamente la lista de productos

    // Generar una cadena de HTML con las tarjetas de producto
    var categoriasHTML = categorias.map(function(categoria) {
      return `
          <div class="col">
              <div class="card h-100">
              <img src="${categoria.categoryImage}" class="card-img-top imgCategorias" alt="${categoria.name}">
              <div class="card-body">
                  <h1 class="card-title">${categoria.name}</h1>
                  <h4 class="card-text">${categoria.description}</h4>
              </div>
              </div>
          </div>
      `;
    }).join('');

    // Obtener la referencia al contenedor de productos
    var categoriasList = document.getElementById('category-list');

    // Insertar las tarjetas de producto en el contenedor
    categoriasList.innerHTML = categoriasHTML;
  } else {
    console.error('Error al realizar la solicitud.');
  }
};
request.onerror = function() {
  console.error('Error de conexi√≥n.');
};
request.send();