var url = 'http://127.0.0.1:8000/orders/getproducts'; // Reemplazar con la URL de tu API de productos

var request = new XMLHttpRequest();
request.open('GET', url, true);
request.onload = function() {
  if (request.status >= 200 && request.status < 400) {
    var response = JSON.parse(request.responseText);
    var productos = response; // Supongamos que la respuesta contiene directamente la lista de productos

    // Generar una cadena de HTML con las tarjetas de producto
    var productosHTML = productos.map(function(producto) {
      return `
          <div class="col-md-4">
              <div class="card h-100 mb-3">
              <img src="${producto.productImage}" class="card-img-top imgCategorias" alt="${producto.name}">
              <div class="card-body">
                  <h1 class="card-title">${producto.name}</h1>
                  <h4 class="card-text">${producto.description}</h4>
                  <h5 class="card-text">Precio: $${producto.sellPrice}</h5>
              </div>
              </div>
          </div>
      `;
    }).join('');

    // Obtener la referencia al contenedor de productos
    var productosList = document.getElementById('productos-list');

    // Insertar las tarjetas de producto en el contenedor
    productosList.innerHTML = productosHTML;
  } else {
    console.error('Error al realizar la solicitud.');
  }
};
request.onerror = function() {
  console.error('Error de conexi√≥n.');
};
request.send();