var url = 'http://127.0.0.1:8000/orders/getSnackProducts'; // Reemplazar con la URL de tu API de productos

var request = new XMLHttpRequest();
request.open('GET', url, true);
request.onload = function() {
  if (request.status >= 200 && request.status < 400) {
    var response = JSON.parse(request.responseText);
    var snack = response; // Supongamos que la respuesta contiene directamente la lista de productos

    // Generar una cadena de HTML con las tarjetas de producto
    var snackHTML = snack.map(function(snack) {
      return `
          <div class="col-md-4">
              <div class="card h-100 mb-3">
              <img src="${snack.productImage}" class="card-img-top imgCategorias" alt="${snack.name}">
              <div class="card-body">
                  <h1 class="card-title">${snack.name}</h1>
                  <h4 class="card-text">${snack.description}</h4>
                  <h5 class="card-text">Precio: $${snack.sellPrice}</h5>
              </div>
              </div>
          </div>
      `;
    }).join('');

    // Obtener la referencia al contenedor de productos
    var snackList = document.getElementById('snack-list');

    // Insertar las tarjetas de producto en el contenedor
    snackList.innerHTML = snackHTML;
  } else {
    console.error('Error al realizar la solicitud.');
  }
};
request.onerror = function() {
  console.error('Error de conexi√≥n.');
};
request.send();