var url = 'http://127.0.0.1:8000/orders/getToyProducts'; // Reemplazar con la URL de tu API de productos

var request = new XMLHttpRequest();
request.open('GET', url, true);
request.onload = function() {
  if (request.status >= 200 && request.status < 400) {
    var response = JSON.parse(request.responseText);
    var toy = response; // Supongamos que la respuesta contiene directamente la lista de productos

    // Generar una cadena de HTML con las tarjetas de producto
    var toyHTML = toy.map(function(toy) {
      return `
          <div class="col-md-4">
              <div class="card h-100 mb-3">
              <img src="${toy.productImage}" class="card-img-top imgCategorias" alt="${toy.name}">
              <div class="card-body">
                  <h1 class="card-title">${toy.name}</h1>
                  <h4 class="card-text">${toy.description}</h4>
                  <h5 class="card-text">Precio: $${toy.sellPrice}</h5>
              </div>
              </div>
          </div>
      `;
    }).join('');

    // Obtener la referencia al contenedor de productos
    var toyList = document.getElementById('toy-list');

    // Insertar las tarjetas de producto en el contenedor
    toyList.innerHTML = toyHTML;
  } else {
    console.error('Error al realizar la solicitud.');
  }
};
request.onerror = function() {
  console.error('Error de conexi√≥n.');
};
request.send();